  }
});
When(/^Set HEADER param request "([^"]*)?" as "([^"]*)?"$/, async (header_name, header_value) => {
  http_request_header[header_name] = header_value;
});
When(/^Raise "([^"]*)?" HTTP request for "([^"]*)?"$/, async (http_request_type, api_path) => {
  let config = {
